testTemplate:
options(error=traceback)
library(testwhat)
.solution <- "${solutionEscaped}"

.solutionfile <- "${solutionFileEscaped}"


setup_state(sol_code = .solutionfile, stu_code = .solution)

tryCatch({
    ${test}
    cat(paste("\033[32m", tw$get()$success_msg, "\033[0m", sep = ""))
}, error = function(e) {
    cat(paste("\033[31m", e[1], "\033[0m", sep = ""))
})
